import readline from "readline/promises";

const possibleAnswers = "".match(/\d{1,3}/g);
const answer = possibleAnswers[Math.floor(Math.random() * possibleAnswers.length)];

const rl = readline.createInterface({
	input: process.stdin,
	output: process.stdout
});

while (true) {
	const guess = await rl.question("숫자를 입력해주세요 (1~9, 3글자): ");
	if (!possibleAnswers.includes(guess)) {
		console.log("1부터 9까지의 서로 다른 수로 이루어진 3자리의 수를 입력해주세요.");
		continue;
	}
	const strike = (answer[0] === guess[0]) + (answer[1] === guess[1]) + (answer[2] === guess[2]);
	const ball = (answer[0] === guess[1]) + (answer[0] === guess[2]) + (answer[1] === guess[0]) + (answer[1] === guess[2]) + (answer[2] === guess[0]) + (answer[2] === guess[1]);
	if (strike === 3) {
		console.log("3개의 숫자를 모두 맞췄습니다.");
		break;
	}
	const hint = [];
	if (strike > 0)
		hint.push(strike + "×스트라이크");
	if (ball > 0)
		hint.push(ball + "×볼");
	if (strike === 0 && ball === 0)
		hint.push("낫싱"); // 싱싱한 낫
	console.log(hint.join(", "));
}

rl.close();